let names = $input.all().map(e => e.json.Name);
let unique_names = [...new Set(names)];
let results = [];

for (thisName of unique_names) {
  let result = {
    "Name": thisName,
    "Days": 0,
    "Type": "Holiday"
  }

  for (matching_item of $input.all().filter(e => e.json.Name === thisName)) {
    result.Days += parseInt(matching_item.json.Days);
  }

  results.push(result);
}

return results.map(e => { return {json: e} });

