let illnessMessage = "";
let holidayMessage = "";
let message = "Here is a breakdown of absences for the last month.\n\n";

// Loop the input items
for (item of $input.all()) {
  if (item.json.Type == "Holiday") {
    holidayMessage += item.json.Name + " had " + item.json.Days + " days\n";
  }
  if (item.json.Type == "Illness") {
    illnessMessage += item.json.Name + " had " + item.json.Days + " days\n";
  }
}

if (holidayMessage != "") {
  message += "Holiday Events\n";
  message += holidayMessage + "\n";
} else {
  message += "No Holiday Events\n";
}

if (illnessMessage != "") {
  message += "Illness Events\n";
  message += illnessMessage;
} else {
  message += "No Illness Events\n";
}

// Return our message
return [{json: {message}}];
